def neo():
    import pyautogui as py
    import time

    while True:

        star = py.locateOnScreen("star.PNG", confidence=0.6)
        if star:
            x, y = py.center(star)
            py.leftClick(x, y)

            time.sleep(2)

            # searches for the desired color on the screen
            redcircle = py.locateOnScreen(
                'circle.png', grayscale=False, confidence=0.6)
            if redcircle:
                x, y = py.center(redcircle)
                py.leftClick(x, y)

                time.sleep(2)

                close = py.locateOnScreen(
                    'clos.png', grayscale=False, confidence=0.6)
                if close:
                    x, y = py.center(close)
                    py.leftClick(x, y)
        nex = py.locateOnScreen('next.png', grayscale=False, confidence=0.5)
        if nex:
            x, y = py.center(nex)
            py.leftClick(x, y)
        else:
            close = py.locateOnScreen('close.PNG', grayscale=False, confidence=0.6)
            if close:
                x, y = py.center(close)
                py.leftClick(x, y)
        
